import React, { useEffect, useState } from "react";
import Style from "../gallery/gallery.module.css";
//import Style from "../../../node_modules/bootstrap/dist/css/bootstrap.css";
import axios from "axios";



const Gallery= () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    getData();
  },[]);

  const getData = async () => {
    try {
      const resp = await axios.get("http://localhost:4000/api/post");
      setPosts(resp.data.reverse());
    } catch (err) {
      console.log({ error: err });
    }
  };

  const renderPost = () => {
    if (posts.length === 0) {
      return <i className="gg-spinner-two"></i>
    } else {
      return posts.map((post) => (


          <div className="column"><img src={post.image} alt="logo" width="100px" height="100px"/><br/><br/></div>

      ));
    }
  };

  return <div className="home"><div className="row">{renderPost()}</div></div>;
};

export default Gallery;
