import React, { useEffect, useState } from "react";
import Style from "../gallery/gallery.module.css";
import axios from "axios";

import { useHistory } from "react-router-dom";

const Gallery= () => {
  let history = useHistory();
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    getData();
  },[]);

  const getData = async () => {
    try {
      const resp = await axios.get("http://localhost:4000/api/post");
      setPosts(resp.data.reverse());
    } catch (err) {
      console.log({ error: err });
    }
  };

  const renderPost = () => {
    if (posts.length === 0) {
      return <i className="gg-spinner-two"></i>
    } else {
      return posts.map((post) => (


          <img src={post.image} alt="logo" />

      ));
    }
  };

  return <div className={Style.home}>{renderPost()}</div>;
};

export default Gallery;
